SELECT * FROM sc_security_groups  
	where 1=1 
	{@ and code like '%#code#%'}
	{@ and name like '%#name#%'}

SELECT * FROM sc_security_function where 1=1 {@ and parent_id = '#id#'
  or
  parent_id in (
    select id from sc_security_function s1 where s1.parent_id = '#id#'
  )
}
